
USE lovesync;

CREATE TABLE usuarios (
  id INT AUTO_INCREMENT PRIMARY KEY,
  nombre VARCHAR(100) NOT NULL,
  mail VARCHAR(255) NOT NULL UNIQUE,
  passwd VARCHAR(255) NOT NULL,
  fechaNacimiento DATE NOT NULL,
  fechaCreacionCuenta TIMESTAMP DEFAULT CURRENT_TIMESTAMP
) ENGINE=InnoDB;

CREATE TABLE perfil (
  id INT AUTO_INCREMENT PRIMARY KEY,  
  user_id INT NOT NULL,
  genero ENUM('hombre','mujer','otro') NOT NULL,
  genint ENUM('hombre','mujer','todos') NOT NULL,
  bio VARCHAR(500),
  intereses JSON,
  ubicacion VARCHAR(100) NOT NULL,
  LenguajeDelAmor VARCHAR(50) NOT NULL,
  edad INT, -- Nueva columna basada en tus datos

  CONSTRAINT fk_perfil_usuarios
    FOREIGN KEY (user_id)
    REFERENCES usuarios(id)
    ON DELETE CASCADE
) ENGINE=InnoDB;

CREATE TABLE matches (
  id INT AUTO_INCREMENT PRIMARY KEY,
  user_id_1 INT NOT NULL,
  user_id_2 INT NOT NULL,
  estado ENUM('pendiente','aceptado','rechazado','bloqueado') DEFAULT 'pendiente',
  quien_acepta INT,
  fechaMatch TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  fechaActualizacion TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  
  CONSTRAINT fk_matches_user1
    FOREIGN KEY (user_id_1)
    REFERENCES usuarios(id)
    ON DELETE CASCADE,
  
  CONSTRAINT fk_matches_user2
    FOREIGN KEY (user_id_2)
    REFERENCES usuarios(id)
    ON DELETE CASCADE,

  CONSTRAINT fk_matches_quien_acepta
    FOREIGN KEY (quien_acepta)
    REFERENCES usuarios(id)
    ON DELETE SET NULL,
  
  CONSTRAINT ck_usuarios_diferentes
    CHECK (user_id_1 < user_id_2),
  
  UNIQUE KEY unique_match (user_id_1, user_id_2)
) ENGINE=InnoDB;



